var DOMParser = require('xmldom').DOMParser;
var $ = require("jquery");

var Utils = function(){};

Utils.prototype.stringToXML = function(text) {
	
	var doc = new DOMParser().parseFromString(text,"text/xml");
	return doc;

};

Utils.prototype.XMLToString = function(xmlNode) { 
	
	var str = new DOMParser().serializeToString(xmlNode);
	return str;
};

Utils.prototype.JSONToString = function(JSONObject) { 

	return JSON.stringify(JSONObject);
};

Utils.prototype.stringToJSON = function(text) { 

	return JSON.parse(text);
};

module.exports = new Utils(); 